#!/usr/bin/env python3

seq=''
seq2=seq.replace('GAATTC','G^AATTC')
seq3=seq2.split(sep='^')

for n in range(len(seq3)):
    query=seq3[n]
    length=len(seq3[n])
    print(f'start position:{seq.find(query)}, end position: {seq.find(query)+length} and length of fragment is {length}')

seq4=sorted(seq3, key=len, reverse=True)

for n in seq4:
    print(n)