#Dictionaries and sets and file I/O
#1
nt_count={}
sequence=''
unique=set(sequence)

for nt in unique:
    count=sequence.count(nt)
    nt_count[nt]=count

print(f'Number of {nt}:{nt_count[nt]}')

gc_count=sequence.count('G')+sequence.count('C')
tot_count=len(sequence)
gc_content=gc_count/tot_count

print(f'GC content: {gc_content:.0%}')
